CREATE VIEW `acuerdos`.`vacuerdos` AS select `a`.`IDAcuerdo` AS `IDAcuerdo`,`a`.`Territorio` AS `Territorio`,`a`.`segmento` AS `Segmento`,`a`.`TipoDoc` AS `TipoDoc`,`a`.`Supra` AS `CIF`,`a`.`AcuerdoNumero` AS `AcuerdoNumero`,`a`.`Nombre` AS `Nombre`,`f`.`ImporteFijo` AS `ImporteFijoMT`,`f`.`ImporteFijoNM` AS `ImporteFijoNM`,`f`.`DescuentoPlana` AS `DescuentoPlanaMT`,`f`.`DescuentoPlanaNM` AS `DescuentoPlanaNM`,`f`.`ModalidadConcertada` AS `ModalidadConcertada`,`f`.`Planaautoajustable` AS `PlanaAutoajustable`,`f`.`Planasimplificada` AS `Planasimplificada`,`f`.`NuevoMercado` AS `NuevoMercado`,`a`.`TipoAcuerdo` AS `TipoAcuerdo`,`t`.`Descripcion` AS `DescTipoAcuerdo`,`a`.`Operadora` AS `Operadora`,if((`a`.`BAJA` = 'True'),1,0) AS `Baja`,`a`.`Fecha_Baja` AS `FechaBaja`,`a`.`CausaBaja` AS `CausaBaja` from ((`acuerdos`.`acuerdos` `a` left join `acuerdos`.`fija_acuerdos` `f` on((`a`.`IDAcuerdo` = `f`.`IDAcuerdo`))) left join `acuerdos`.`tipoacuerdo` `t` on((`a`.`TipoAcuerdo` = `t`.`TipoAcuerdo`))) where (1 = 1);
